package javapractice1.wk3;

public class OperatorEx1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = -100;
		int result_x1 = +x;
		int result_x2 = -x;
		System.out.println("reslut_x1: " + result_x1);
		System.out.println("reslut_x2: " + result_x2);
		
		short s = 50;
		int result_s2 = -s;
		int result_s3 = (short)-s;
		System.out.println("reslut_x2: " + result_s2);
		System.out.println("reslut_s3: " + result_s3);
	}
}
