package javapractice1.wk3;

public class OperatorEx2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int v1 = 5;
		int v2 = 2;
		
		int result1 = v1 + v2;
		System.out.println("reslut1: " + result1);
		
		int result2 = v1 - v2;
		System.out.println("reslut2: " + result2);
		
		int result3 = v1 * v2;
		System.out.println("reslut3: " + result3);
		
		double result4 = (double)v1 / (double)v2;
		System.out.println("reslut4d: " + result4);
		
		int result5 = v1 % v2;
		System.out.println("reslut5: " + result5);
	}
}
