package javapractice1.wk3;

public class OperatorEx5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int result = 0;
		result += 10;
		System.out.println("reslrt: "+result);
		result -= 5;
		System.out.println("reslrt: "+result);
		result *= 3;
		System.out.println("reslrt: "+result);
		result /= 5;
		System.out.println("reslrt: "+result);
		result %= 2;
		System.out.println("reslrt: "+result);
	}
}
